contracts/EmergencyMode.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/**
 * @title EmergencyMode
 * @dev Government-activated emergency controller; PaymentRouter can consult this to waive fees
 */
contract EmergencyMode {
    address public immutable admin;

    // Global emergency switch
    bool public globalEmergency;

    // Region-based emergency by code (e.g., bytes32("UG-Kampala"))
    mapping(bytes32 => bool) public regionEmergency;

    // Authorized government/NGO accounts allowed to toggle emergencies
    mapping(address => bool) public isAuthority;

    event AuthorityUpdated(address indexed authority, bool allowed);
    event GlobalEmergencySet(bool active, uint256 timestamp);
    event RegionEmergencySet(bytes32 indexed region, bool active, uint256 timestamp);

    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin");
        _;
    }

    modifier onlyAuthority() {
        require(msg.sender == admin || isAuthority[msg.sender], "Only authority");
        _;
    }

    constructor() {
        admin = msg.sender;
    }

    function setAuthority(address authority, bool allowed) external onlyAdmin {
        isAuthority[authority] = allowed;
        emit AuthorityUpdated(authority, allowed);
    }

    function setGlobalEmergency(bool active) external onlyAuthority {
        globalEmergency = active;
        emit GlobalEmergencySet(active, block.timestamp);
    }

    function setRegionEmergency(bytes32 region, bool active) external onlyAuthority {
        regionEmergency[region] = active;
        emit RegionEmergencySet(region, active, block.timestamp);
    }

    function isEmergency(bytes32 region) external view returns (bool) {
        return globalEmergency || regionEmergency[region];
    }
}

contracts/MiniToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

contract MiniToken {
  string public name = "Mini";
  string public symbol = "MIN";
  uint8 public decimals = 18;
  mapping(address=>uint256) public balanceOf;
  mapping(address=>mapping(address=>uint256)) public allowance;
  event Transfer(address indexed from,address indexed to,uint256 value);
  event Approval(address indexed owner,address indexed spender,uint256 value);
  constructor(){balanceOf[msg.sender]=1e24;}
  function transfer(address to,uint256 amount) external returns(bool){require(balanceOf[msg.sender]>=amount,"bal");unchecked{balanceOf[msg.sender]-=amount;balanceOf[to]+=amount;}emit Transfer(msg.sender,to,amount);return true;}
  function approve(address sp,uint256 amount) external returns(bool){allowance[msg.sender][sp]=amount;emit Approval(msg.sender,sp,amount);return true;}
  function transferFrom(address from,address to,uint256 amount) external returns(bool){require(balanceOf[from]>=amount,"bal");require(allowance[from][msg.sender]>=amount,"allow");unchecked{allowance[from][msg.sender]-=amount;balanceOf[from]-=amount;balanceOf[to]+=amount;}emit Transfer(from,to,amount);return true;}
}

contracts/PaymentRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/**
 * @title PaymentRouter - Main payment processing contract
 * @dev Handles P2P transfers with social messages and multi-token support
 * @notice For beginners: think of this as a simple bank ledger on Base Sepolia
 *
 * KEY CONCEPTS:
 * - msg.sender: Address that called the function
 * - address: Like a bank account number on blockchain
 * - require(): A rule that must be true or the transaction reverts
 * - event: A log for off-chain apps (frontends/indexers) to watch
 *
 * BASE L2 BENEFITS:
 * - Very low fees and fast confirmations while inheriting Ethereum security
 */
interface IERC20 {
    function transfer(address to, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
    function allowance(address owner, address spender) external view returns (uint256);
    function decimals() external view returns (uint8);
}

contract PaymentRouter {
    // Admin address that can toggle emergency mode; in a future version, controlled by EmergencyMode contract
    address public immutable admin;

    // Global emergency flag (fee-free transfers when true); extendable to region-based in future
    bool public emergencyActive;

    // Flat fee in wei for ETH transfers when not in emergency; default 0 for test, can be updated by admin later
    uint256 public flatEthFeeWei;

    // Mapping to track number of payments sent per user (simple reputation signal)
    mapping(address => uint256) public paymentsSentCount;

    // Mapping to track number of payments received per user
    mapping(address => uint256) public paymentsReceivedCount;

    /// @dev Payment record structure kept minimal for gas; frontends pull full detail from events
    struct PaymentRecord {
        address from;
        address to;
        address token; // address(0) for native ETH
        uint256 amount;
        uint256 timestamp;
    }

    // Simple per-user circular history: latest N hashes for quick on-chain peek; complete history should be read from events
    uint8 public constant HISTORY_SIZE = 16;
    mapping(address => bytes32[HISTORY_SIZE]) private userRecentPayments;
    mapping(address => uint8) private userRecentIndex;

    /// @notice Emitted on every payment
    event PaymentSent(
        address indexed from,
        address indexed to,
        address indexed token,
        uint256 amount,
        string message,
        uint256 timestamp
    );

    /// @notice Emitted when emergency toggles
    event EmergencyModeUpdated(bool active, uint256 timestamp);

    constructor() {
        admin = msg.sender; // Deployer becomes admin
        flatEthFeeWei = 0; // Start with zero fee to simplify testing
    }

    /**
     * @notice Toggle global emergency mode (admin-only)
     * @dev In emergencies, fees are waived to support crisis payments
     */
    function setEmergencyActive(bool active) external {
        require(msg.sender == admin, "Only admin");
        emergencyActive = active;
        emit EmergencyModeUpdated(active, block.timestamp);
    }

    /**
     * @notice Update the flat ETH fee (admin-only). Ignored while emergencyActive.
     */
    function setFlatEthFeeWei(uint256 newFee) external {
        require(msg.sender == admin, "Only admin");
        flatEthFeeWei = newFee;
    }

    /**
     * @notice Send native ETH with an optional social message
     * @param to Recipient address
     * @param message Social note (stored in event only)
     */
    function payETH(address to, string calldata message) external payable {
        require(to != address(0), "Invalid to");
        require(msg.value > 0, "No value");

        uint256 valueToSend = msg.value;
        if (!emergencyActive && flatEthFeeWei > 0) {
            require(valueToSend > flatEthFeeWei, "Fee exceeds amount");
            unchecked {
                valueToSend -= flatEthFeeWei;
            }
            // fee retained in contract; in production, route to treasury
        }

        // Transfer remaining ETH to recipient
        (bool ok, ) = to.call{value: valueToSend}("");
        require(ok, "ETH transfer failed");

        _afterPayment(msg.sender, to, address(0), valueToSend, message);
    }

    /**
     * @notice Send ERC-20 tokens (e.g., USDC) with a message
     * @dev Sender must approve this contract to spend tokens first via token.approve()
     * @param token ERC-20 token address
     * @param to Recipient address
     * @param amount Token amount in smallest units (e.g., 6 decimals for USDC)
     * @param message Social note (event-only)
     */
    function payERC20(address token, address to, uint256 amount, string calldata message) external {
        require(to != address(0), "Invalid to");
        require(token != address(0), "Invalid token");
        require(amount > 0, "No amount");

        IERC20 erc20 = IERC20(token);

        // Pull tokens from sender; requires prior approval
        bool ok = erc20.transferFrom(msg.sender, to, amount);
        require(ok, "transferFrom failed");

        _afterPayment(msg.sender, to, token, amount, message);
    }

    /**
     * @notice Return a user's last up to HISTORY_SIZE payment hashes
     * @dev Frontends typically index events; this is a lightweight on-chain glimpse
     */
    function getRecentPayments(address user) external view returns (bytes32[HISTORY_SIZE] memory) {
        return userRecentPayments[user];
    }

    function _afterPayment(
        address from,
        address to,
        address token,
        uint256 amount,
        string calldata message
    ) internal {
        // Update simple counters
        unchecked {
            paymentsSentCount[from] += 1;
            paymentsReceivedCount[to] += 1;
        }

        // Record circular history via a hash (from,to,token,amount,ts)
        uint8 idx = userRecentIndex[from];
        bytes32 entry = keccak256(abi.encode(from, to, token, amount, block.timestamp));
        userRecentPayments[from][idx] = entry;
        userRecentIndex[from] = (idx + 1) % HISTORY_SIZE;

        emit PaymentSent(from, to, token, amount, message, block.timestamp);
    }

    // Allow contract to receive ETH (from fees or accidental sends)
    receive() external payable {}
}

contracts/SavingsCircles.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

interface IERC20 {
    function transfer(address to, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
    function decimals() external view returns (uint8);
}

/**
 * @title SavingsCircles
 * @dev Minimal ROSCA implementation for rotating payouts using an ERC-20 token
 */
contract SavingsCircles {
    struct Circle {
        address organizer;
        IERC20 token;
        uint256 contribution;
        uint256 periodSeconds;
        address[] members;
        uint256 currentIndex; // who receives the next payout
        uint256 nextPayoutTime;
        mapping(uint256 => mapping(address => bool)) hasDepositedForRound; // round -> member -> deposited
        uint256 round; // increments each payout
        bool active;
    }

    uint256 public nextId = 1;
    mapping(uint256 => Circle) private circles;

    event CircleCreated(uint256 indexed id, address indexed organizer, address token, uint256 contribution, uint256 period, address[] members);
    event Deposited(uint256 indexed id, uint256 indexed round, address indexed member, uint256 amount);
    event Payout(uint256 indexed id, uint256 indexed round, address indexed recipient, uint256 amount);

    function createCircle(IERC20 token, address[] calldata members, uint256 contribution, uint256 periodSeconds) external returns (uint256 id) {
        require(address(token) != address(0), "token=0");
        require(members.length >= 2, "members<2");
        require(contribution > 0, "contribution=0");
        require(periodSeconds >= 60, "period too small");

        id = nextId++;
        Circle storage c = circles[id];
        c.organizer = msg.sender;
        c.token = token;
        c.contribution = contribution;
        c.periodSeconds = periodSeconds;
        c.members = members;
        c.currentIndex = 0;
        c.nextPayoutTime = block.timestamp + periodSeconds;
        c.round = 0;
        c.active = true;

        emit CircleCreated(id, msg.sender, address(token), contribution, periodSeconds, members);
    }

    function getCircle(uint256 id) external view returns (
        address organizer,
        address token,
        uint256 contribution,
        uint256 periodSeconds,
        address[] memory members,
        uint256 currentIndex,
        uint256 nextPayoutTime,
        uint256 round,
        bool active
    ) {
        Circle storage c = circles[id];
        organizer = c.organizer;
        token = address(c.token);
        contribution = c.contribution;
        periodSeconds = c.periodSeconds;
        members = c.members;
        currentIndex = c.currentIndex;
        nextPayoutTime = c.nextPayoutTime;
        round = c.round;
        active = c.active;
    }

    function deposit(uint256 id) external {
        Circle storage c = circles[id];
        require(c.active, "inactive");
        require(_isMember(c, msg.sender), "not member");
        require(!c.hasDepositedForRound[c.round][msg.sender], "already");
        require(c.token.transferFrom(msg.sender, address(this), c.contribution), "transferFrom fail");
        c.hasDepositedForRound[c.round][msg.sender] = true;
        emit Deposited(id, c.round, msg.sender, c.contribution);
    }

    function canPayout(uint256 id) public view returns (bool) {
        Circle storage c = circles[id];
        if (!c.active) return false;
        if (block.timestamp < c.nextPayoutTime) return false;
        // Ensure every member has deposited for this round
        for (uint256 i = 0; i < c.members.length; i++) {
            if (!c.hasDepositedForRound[c.round][c.members[i]]) return false;
        }
        return true;
    }

    function payout(uint256 id) external {
        Circle storage c = circles[id];
        require(canPayout(id), "not ready");
        address recipient = c.members[c.currentIndex];
        uint256 pot = c.contribution * c.members.length;
        require(c.token.transfer(recipient, pot), "transfer fail");
        emit Payout(id, c.round, recipient, pot);

        // advance state
        c.round += 1;
        c.currentIndex = (c.currentIndex + 1) % c.members.length;
        c.nextPayoutTime = block.timestamp + c.periodSeconds;
    }

    function _isMember(Circle storage c, address a) internal view returns (bool) {
        for (uint256 i = 0; i < c.members.length; i++) {
            if (c.members[i] == a) return true;
        }
        return false;
    }
}

contracts/SocialReputation.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

interface IERC20 {
    function transfer(address to, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
    function decimals() external view returns (uint8);
}

/**
 * @title SocialReputation
 * @dev On-chain reputation scoring and simple micro-loans backed by a token pool
 */
contract SocialReputation {
    address public immutable admin;
    IERC20 public immutable loanToken; // e.g., USDC on Base Sepolia

    // Global epoch to allow admin to reset reputation across all users
    uint256 public currentEpoch = 1;

    // Authorized callers (e.g., PaymentRouter) that can report payments
    mapping(address => bool) public isAuthorizedCaller;

    struct Metrics {
        uint256 sentCount;
        uint256 receivedCount;
        uint256 volume; // sum of amounts across tokens normalized 1e18 by caller
        uint256 endorsements; // number of unique endorsements (for current epoch)
        // Track endorsements per epoch to allow re-endorsement after resets
        mapping(uint256 => mapping(address => bool)) hasEndorsed; // epoch -> endorser -> bool
    }

    mapping(address => Metrics) private userMetrics;
    // For counters (sent/received/volume/endorsements), track which epoch a user's
    // metrics are aligned to. If stale, we reset on first write in new epoch.
    mapping(address => uint256) public metricsEpoch;

    // Pool accounting using shares (proportional claims)
    uint256 public totalPool; // total token balance managed by the pool
    uint256 public totalShares; // sum of all userShares
    mapping(address => uint256) public userShares; // depositor shares

    // Simple loan book (one loan at a time per user for demo)
    mapping(address => uint256) public outstandingDebt; // principal owed
    uint256 public interestBps = 200; // 2% flat interest per loan (for demo)

    // Reputation boosts for funding: flat bonuses per funding action in current epoch
    mapping(address => uint256) public fundingBonus; // additive to score (per epoch)
    mapping(address => uint256) public fundingBonusEpoch;

    event AuthorizedCaller(address indexed caller, bool allowed);
    event PaymentReported(address indexed from, address indexed to, address token, uint256 amount);
    event Endorsed(address indexed endorser, address indexed target);
    event Unendorsed(address indexed endorser, address indexed target);
    event PoolFunded(address indexed from, uint256 amount, uint256 sharesMinted);
    event PoolWithdrawn(address indexed to, uint256 amount, uint256 sharesBurned);
    event Borrowed(address indexed borrower, uint256 amount);
    event Repaid(address indexed borrower, uint256 amount);

    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin");
        _;
    }

    modifier onlyAuthorized() {
        require(isAuthorizedCaller[msg.sender], "Not authorized");
        _;
    }

    constructor(IERC20 _loanToken) {
        admin = msg.sender;
        loanToken = _loanToken;
    }

    // Internal: reset user metrics if their epoch is stale
    function _syncUserEpoch(address user) internal {
        if (metricsEpoch[user] != currentEpoch) {
            Metrics storage m = userMetrics[user];
            m.sentCount = 0;
            m.receivedCount = 0;
            m.volume = 0;
            m.endorsements = 0;
            metricsEpoch[user] = currentEpoch;
        }
    }

    // --- Reputation core ---
    function setAuthorizedCaller(address caller, bool allowed) external onlyAdmin {
        isAuthorizedCaller[caller] = allowed;
        emit AuthorizedCaller(caller, allowed);
    }

    // Caller should normalize `amount` to 1e18 scale for fair scoring
    function reportPayment(address from, address to, address token, uint256 amountNormalized1e18) external onlyAuthorized {
        _syncUserEpoch(from);
        _syncUserEpoch(to);
        Metrics storage mf = userMetrics[from];
        Metrics storage mt = userMetrics[to];
        unchecked {
            mf.sentCount += 1;
            mt.receivedCount += 1;
            mf.volume += amountNormalized1e18;
            mt.volume += amountNormalized1e18;
        }
        emit PaymentReported(from, to, token, amountNormalized1e18);
    }

    function endorse(address target) external {
        _syncUserEpoch(target);
        Metrics storage m = userMetrics[target];
        require(!m.hasEndorsed[currentEpoch][msg.sender], "Already endorsed");
        m.hasEndorsed[currentEpoch][msg.sender] = true;
        unchecked { m.endorsements += 1; }
        emit Endorsed(msg.sender, target);
    }

    function unendorse(address target) external {
        _syncUserEpoch(target);
        Metrics storage m = userMetrics[target];
        require(m.hasEndorsed[currentEpoch][msg.sender], "Not endorsed");
        m.hasEndorsed[currentEpoch][msg.sender] = false;
        unchecked { m.endorsements -= 1; }
        emit Unendorsed(msg.sender, target);
    }

    function getScore(address user) public view returns (uint256) {
        uint256 score = 0;
        if (metricsEpoch[user] == currentEpoch) {
            Metrics storage m = userMetrics[user];
            // Simple weighted scoring: counts + endorsements + volume factor
            // Weights chosen for demo purposes
            score = m.sentCount * 2 + m.receivedCount * 1 + m.endorsements * 10 + m.volume / 1e20; // volume dampened
        }
        if (fundingBonusEpoch[user] == currentEpoch) {
            score += fundingBonus[user];
        }
        return score;
    }

    // --- Micro-loans ---
    function fundPool(uint256 amount) external {
        require(amount > 0, "amount=0");
        bool ok = loanToken.transferFrom(msg.sender, address(this), amount);
        require(ok, "transferFrom fail");
        uint256 shares;
        if (totalShares == 0 || totalPool == 0) {
            shares = amount; // 1:1 initial pricing
        } else {
            shares = (amount * totalShares) / totalPool;
        }
        require(shares > 0, "shares=0");
        totalPool += amount;
        totalShares += shares;
        userShares[msg.sender] += shares;
        // Add flat reputation bonus per funding action (20 points)
        if (fundingBonusEpoch[msg.sender] != currentEpoch) {
            fundingBonus[msg.sender] = 0;
            fundingBonusEpoch[msg.sender] = currentEpoch;
        }
        unchecked { fundingBonus[msg.sender] += 20; }
        emit PoolFunded(msg.sender, amount, shares);
    }

    function getUserStakeValue(address user) public view returns (uint256) {
        if (totalShares == 0) return 0;
        return (userShares[user] * totalPool) / totalShares;
    }

    // Withdraw exact token amount (partial allowed)
    function withdrawAmount(uint256 amount) external {
        require(amount > 0, "amount=0");
        require(totalPool > 0 && totalShares > 0, "empty");
        uint256 shares = (amount * totalShares) / totalPool;
        require(shares > 0 && userShares[msg.sender] >= shares, "shares");
        userShares[msg.sender] -= shares;
        totalShares -= shares;
        totalPool -= amount;
        require(loanToken.transfer(msg.sender, amount), "transfer fail");
        emit PoolWithdrawn(msg.sender, amount, shares);
    }

    // Withdraw by shares
    function withdrawShares(uint256 shares) external {
        require(shares > 0 && userShares[msg.sender] >= shares, "shares");
        require(totalShares > 0, "no shares");
        uint256 amount = (shares * totalPool) / totalShares;
        userShares[msg.sender] -= shares;
        totalShares -= shares;
        totalPool -= amount;
        require(loanToken.transfer(msg.sender, amount), "transfer fail");
        emit PoolWithdrawn(msg.sender, amount, shares);
    }

    function maxBorrowable(address user) public view returns (uint256) {
        // Very simple policy: max = min(pool, score * 10^loanDecimals / 100)
        uint8 dec = loanToken.decimals();
        uint256 base = getScore(user) * (10 ** dec) / 100; // 1% of score in token units
        if (base > totalPool) return totalPool;
        return base;
    }

    function borrow(uint256 amount) external {
        require(outstandingDebt[msg.sender] == 0, "Debt exists");
        uint256 maxAmt = maxBorrowable(msg.sender);
        require(amount > 0 && amount <= maxAmt, "exceeds limit");
        outstandingDebt[msg.sender] = amount; // principal
        totalPool -= amount;
        require(loanToken.transfer(msg.sender, amount), "transfer fail");
        emit Borrowed(msg.sender, amount);
    }

    function repay(uint256 amount) external {
        require(amount > 0, "amount=0");
        uint256 debt = outstandingDebt[msg.sender];
        require(debt > 0, "no debt");
        uint256 due = debt + ((debt * interestBps) / 10000);
        require(amount >= due, "insufficient repay");
        bool ok = loanToken.transferFrom(msg.sender, address(this), amount);
        require(ok, "transferFrom fail");
        outstandingDebt[msg.sender] = 0;
        totalPool += amount; // interest increases pool value for all depositors
        emit Repaid(msg.sender, amount);
    }

    // --- Admin controls ---
    // Increment epoch to reset reputation scoring for all users
    function resetAllReputation() external onlyAdmin {
        unchecked { currentEpoch += 1; }
    }

    // --- Endorsements ---
    // Already one-per-endorser per epoch; allow self-endorse once per epoch
    function selfEndorse() external {
        _syncUserEpoch(msg.sender);
        Metrics storage m = userMetrics[msg.sender];
        require(!m.hasEndorsed[currentEpoch][msg.sender], "Already self-endorsed");
        m.hasEndorsed[currentEpoch][msg.sender] = true;
        unchecked { m.endorsements += 1; }
        emit Endorsed(msg.sender, msg.sender);
    }
}

